startup:
echo "{{PHP_VERSION}}" > "php_version.txt";

if [ "{{GIT_UPDATE}}" = "1" ]; then 
    {{STARTUP_COMMAND_GIT}}; 
fi;

if [ "{{CLOUDFLARED_STATUS}}" = "1" ]; then 
    echo "{{CLOUDFLARED_TOKEN}}" > "cloudflared_token.txt";
    {{STARTUP_COMMAND_CLOUDFLARED}};
fi;

if [[ -n "{{COMPOSER_MODULES}}" ]]; then
  composer require {{COMPOSER_MODULES}} --working-dir=/home/container/www --no-interaction --ansi;
fi;

{{STARTUP_COMMAND}};
